if (!function_exists('getDirectory')) {    
    /**
     * directory
     *
     * @param  mixed $type
     * @return string
     */
    function getDirectory(string $directoryName): string
    {
        $directories = [
            'logo' => 'public/uploads/logos/',
            'favicon' => 'public/uploads/logos/',
            'currency' => 'public/uploads/currency_logos/',
            'profile' => 'public/uploads/user-profile/',
            'merchant' => 'public/uploads/merchant/',
            'user_qrcode' => 'public/uploads/qrcode/user/',
            'merchant_qrcode' => 'public/uploads/qrcode/merchant/',
            'language_flag' => 'public/uploads/languages-flags/',
            'bank_logos' => 'public/uploads/files/bank_logos/',
        ];

        if (!is_dir($directories[$directoryName])) {
            makeDirectory($directories[$directoryName]);
        }
        
        return $directories[$directoryName] ?? '';
    }
}

if (!function_exists('checkFileValidation')) {
    function checkFileValidation($ext, $type = 0)
    {
        return in_array(strtolower($ext), getFileExtensions($type)) ? true : false;
    }
}

if (!function_exists('getFileExtensions')) {
    function getFileExtensions($type = 0)
    {
        $extensions = array(
            0 => ['jpg', 'jpeg', 'png', 'bmp', 'gif', 'doc', 'docx', 'xls', 'xlsx', 'csv', 'pdf'],
            1 => ['jpg', 'jpeg', 'png', 'gif', 'doc', 'docx', 'pdf'],
            2 => ['jpg', 'jpeg', 'png', 'doc', 'docx', 'pdf'],
            3 => ['jpg', 'jpeg', 'png', 'gif', 'bmp'],
            4 => ['ico'],
            5 => ['doc', 'docx', 'xls', 'xlsx', 'csv', 'pdf'],
            6 => ['jpg', 'jpeg', 'png'],
            7 => ['jpg', 'jpeg', 'png', 'gif', 'bmp', 'ico'],
            8 => ['jpg', 'jpeg', 'png', 'gif', 'bmp', 'pdf']
        );
        return $extensions[$type];
    }
}

if (!function_exists('maxFileSize')) {
    /**
     * maxFileSize
     * @param  int $fileSize
     * @return string
     */
    function maxFileSize($fileSize)
    {
        $data = [];
        $maxfileSize = preference('file_size');
        if (isset($maxfileSize) && !empty($maxfileSize)) {
            $maxFileSize = (int) $maxfileSize;
            if (($fileSize / 1024) <= $maxfileSize * 1024) {
                $data['status'] = 1;
            } else if (($fileSize / 1024) > $maxfileSize * 1024) {
                $data['status'] = 0;
                $data['message'] = __('Maximum File Size :x MB.', ['x' => $maxfileSize]);
            }
            return $data;
        }
    }
}

if (!function_exists('fileExistCheck')) {
    /**
     * Check if the file exist in the correct location
     * @param  $file [filename from database or request]
     * @param  $path [path key that define in getDirectory() function]
     * @return string|bool
     */
    function fileExistCheck($file, $path): string|bool
    {
        if (!is_null($file)) {
            $file = base_path(getDirectory($path) . '/' . $file);
            if (file_exists($file)) {
                return $file;
            }
        }

        return false;
    }
}

